def news(html):
    soup = bs4.BeautifulSoup(html)
    h3 = soup.select(".card h3")
    p = soup.select(".card p")
    links = soup.select(".secure")
    smart = {}
    id = 1001
    for i in range(len(h3)):
        add_row(h3[i].getText(),p[i].getText(),links[i].get("href"))
        smart[id] = {"Title":h3[i].getText(),"Description":p[i].getText(),"Links":links[i].get("href")}
        id += 1
    print(smart)

